<?php
    require_once("../db/config.php");
    session_start();

    function insert_food($item){
        $db = dbConnection();
        $sql = "insert into food_menu values('{$item['item_no']}','{$item['item_name']}', '{$item['price']}','{$item['ingradients']}', '{$item['rating']}')";
        $result = mysqli_query($db,$sql);
        if($result){
            echo true;
        }
        else{
            echo false;
        }
    }

    function update_item($item){

    }
